import React from "react";
import "../login/LoginInfo.css"
import { useState} from "react";
import axios from "axios";
const Signup = ({ inputs, title }) =>{

 
  const [info, setInfo] = useState({});

  const handleChange = (e) => {
    setInfo((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };
  const handleClick = async (e) => {
    e.preventDefault();
    try {
      const newUser = {
        ...info,
        
      };

      await axios.post("http://localhost:8800/auth/register", newUser);
    } catch (err) {
      console.log(err);
    }
  };

  console.log("Came");
  return (
    <div className="loginContainer">
      <div className="loginWrapper">
         <form className="loginform" onSubmit={handleClick}>
          <div className="loginTitle">Sign in</div>
          <div>
          <input className="loginInput" type="text" placeholder="First Name" id="firstname" name="firstname"/> 
          <input className="loginInput" type="text" placeholder="Email" id="middlename" name="middlename"/>
          <input className="loginInput" type="text" placeholder="Email" id="lastname" name="lastname"/>

          </div>
          <button className="loginButton" type="submit">Login</button>
      </form>
      </div>
    </div>
  );
};
export default Signup;
